$(function(){$('#currency-input').on('input change',function(){let value=parseFloat($(this).val());$('.convert-me').each(function(i,item){let el=$(item);let conversion=parseFloat(el.data('rate'));el.val(parseFloat(value/conversion).toFixed(2)||0)})});$.ajax({url:'/storage/files/currency.json',success:function(response){let currencies=['ISK','EUR','USD','GBP'];$.grep(response.results,function(item){if($.inArray(item.shortName,currencies)!==-1){$('#'+item.shortName).attr('data-rate',item.value)}})}})})