$(document).ready(function(){$.ajax({'url':'/storage/files/weather.json','success':function(response){let degrees=response.results[0].forecast[1].T
let city=response.results[0].name
let wind=response.results[0].forecast[1].F
let windd=response.results[0].forecast[1].D
$('#degrees').append(degrees);$('#city').append(city);$('#wind').append(wind);$('#windd').append(windd)}})})